import React, { useState } from "react";
// import { Link } from "react-router-dom";
import { Card, Col, Container, Image, Row, Button, Modal} from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";
import "./UserItem.css";

const UserItem = props => {
  const [showDetail, setShowDetail] = useState(false);

  const openDetailHandler = () => setShowDetail(true);
  const closeDetailHandler = () => setShowDetail(false);

  return (
      <Modal show={showDetail} onHide={closeDetailHandler}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body><Card className="bg-dark text-white card__container_list">
          <Row>
            <Col>{props.id}</Col>
            <Col>
              <Image
                src={props.image}
                roundedCircle
                className="image__profile"
              />
              <h1 className="title__card">{props.name}</h1>
              <Container className="card_center">
                <Row>
                  <Col xs={6}>
                    <Card.Link href="#" className="like_dislike">
                      <Image
                        src={require("../../img/iconProfile/heart.png")}
                        width="50px"
                        height="50px"
                      />
                    </Card.Link>
                  </Col>
                  <Col xs={6}>
                    <Card.Link href="#" className="like_dislike">
                      <Image
                        src={require("../../img/iconProfile/remove.png")}
                        width="50px"
                        height="50px"
                      />
                    </Card.Link>
                  </Col>
                </Row>
              </Container>
            </Col>

            <Col><Button onClick={openDetailHandler}> Detail </Button></Col>
          </Row>
        </Card></Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={closeDetailHandler}>
            Close
          </Button>
          <Button variant="primary" onClick={closeDetailHandler}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal>
        
  );
};

export default UserItem;
