import React from "react";
// import { Link } from "react-router-dom";
import { Card, Col, Image, Row } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.css";

import "./NotificationItem.css";

const NotificationItem = props => {
  const Message = () => {
    if (props.type === "Like")
    {
      return <Card.Link href={props.link} className="message_notif"><h3>Like your profile</h3></Card.Link>
    }
    if (props.type === "Visit")
    {
      return <Card.Link href={props.link} className="message_notif"><h3>Visit your profile</h3></Card.Link>
    } 
    if (props.type === "Chat")
    {
      return <Card.Link href={props.link} className="message_notif"><h3>Send you a Message</h3></Card.Link>
    }
  }
  console.log(props.image);
  return (
    <Card className="bg-dark text-white card__container">
      <Row>
        <Col>
          <Image src={props.image} roundedCircle className="image__notif" />
          <h3 className="title__card">{props.name}</h3>
        </Col>
        <Col>
                
                  {Message()}
        </Col>
        <Col><h3>{props.date}</h3></Col>
      </Row>
    </Card>
  );
};

export default NotificationItem;
